const ok = function(msg="success", dt=[]){
	// console.log(arguments);
	
	return typeof arguments[0]=="object" 
		? {st:1, msg:'success', dt:arguments[0]}
		: {st:1, msg, dt};
}

const err = function(msg="fail", data=[]){
	
	return typeof arguments[0]=="object"
		? {st:0, msg:'fail', dt:arguments[0]}
		: {st:0, msg, dt};
}

// 导出日志实例
module.exports = {
	ok,
	err,
};

